import './Livre.css';
import { useState, useEffect } from 'react';

function Livre(props) {

    const [NbTome, setNbTome] = useState([])

    
    useEffect(() => {
        fetch("https://localhost:7152/api/Tomes/NbTomeParLivre")
          .then((result) => {
            result.json().then((resp) => {
                setNbTome(resp);
                console.log(resp)
            }).catch(function (error) {
              console.log("error : ", error);
            })
          })
      }, [])

    return (
        <div className='Livre'>
            <div className='h4'>{props.livre.nom}</div>
            <p>/ {NbTome}</p>
        </div>
    );
}

export default Livre;
