import './Livre.css';

function Livre(props) {

    const afficheDescription = () => {
        props.func(props.index);
    }

    return (
        <div className='Livre'>
            <h1>{props.livre.nom}</h1>
            <p>nb tomes</p>
            <button onClick={afficheDescription}>description &#11015;</button>
            <p className={props.isActive ? 'active' : 'album'}>{props.livre.resumer}</p>
        </div>
    );
}

export default Livre;


