import './css/App.css';
import ListLivres from './ListLivres/ListLivres.js';
import React, {useEffect, useState} from 'react';

function App() {

  const [books, setBooks] = useState([])
  const [allBooks, setAllBooks] = useState([])

  useEffect(() => {
    fetch("https://localhost:7152/api/Livres/AllLivres")
    .then((result) => {
      result.json().then((resp) => {
        setAllBooks(resp);
        setBooks(resp);
      }).catch(function (error) {
        console.log("error : ", error);
      })
    })
  }, [])

  const FilterBooks = (event) => {
    if(event.target.value == "")
    {
      setBooks(allBooks);
    }
    else
    {
      const results = allBooks.filter(book => {
        console.log(book.nom.startsWith(event.target.value))
        return book.nom.startsWith(event.target.value);
      })
      setBooks(results);
    }
  }

  return (
    <>
      <input placeholder="rechercher" onChange={FilterBooks}/>
      <ListLivres books={books}/>
    </>
  );
}

export default App;
