import './ListLivres.css';
import Livre from './../Livre/Livre.js';
import { useState } from 'react';

function ListLivres(props) {

  const [activeIndex, setActif] = useState(null);



  const afficheDescription = (index) => {
    if (index == activeIndex) {
      setActif(null)
    }
    else {
      setActif(index)
    }
  }

  return (
    <>
      {
        props.books.map((element, index) => (
          <Livre livre={element} index={index} isActive={activeIndex === index} func={afficheDescription} />
        ))
      }
    </>
  );
}

export default ListLivres;
