import './css/App.css';
import ListLivres from './ListLivres/ListLivres.js';
import Menu from './Menu/Menu.js';
import React, { useEffect, useState } from 'react';

function App() {

  const [books, setBooks] = useState([])
  const [allBooks, setAllBooks] = useState([])

  useEffect(() => {
    fetch("https://localhost:7152/api/Livres/AllLivres")
      .then((result) => {
        result.json().then((resp) => {
          setAllBooks(resp);
          setBooks(resp);
        }).catch(function (error) {
          console.log("error : ", error);
        })
      })
  }, [])

  const FilterBooks = (event) => {
    const val = event.target.value.charAt(0).toUpperCase()
    if (val == "") {
      setBooks(allBooks);
    }
    else {
      const results = allBooks.filter(book => {
        console.log(book.nom.startsWith(val))
        return book.nom.startsWith(val);
      })
      setBooks(results);
    }
  }

  return (
    <>
      <div>
        <Menu />
        <div className='laList'>
          <input placeholder="rechercher" onChange={FilterBooks} />
          <ListLivres books={books} />
        </div>
      </div>
    </>
  );
}

export default App;
