import './css/App.css';
import ListLivres from './ListLivres/ListLivres.js';
import React, {useEffect, useState} from 'react';

function App() {

  const [books, setBooks] = useState([])

  useEffect(() => {
    fetch("https://localhost:7152/api/Livres/AllLivres")
    .then((result) => {
      result.json().then((resp) => {
        setBooks(resp);
      }).catch(function (error) {
        console.log("error : ", error);
      })
    })
  })

  const FilterBooks = (event) => {
    const results = books.filter(book => {
      return book.nom.startsWith(event.target.value);
    })
    setBooks(results);
    console.log(books);
  }

  return (
    <>
      <input placeholder="rechercher" onChangeFilter={FilterBooks}/>
      <ListLivres books={books}/>
    </>
  );
}

export default App;
