import './css/App.css';
import ListLivres from './ListLivres/ListLivres.js';
import React, {useEffect, useState} from 'react';

function App() {

  const [books, setBooks] = useState([])
  const [filterBooks, setFilterBooks] = useState([])

  useEffect(() => {
    fetch("https://localhost:7152/api/Livres/AllLivres")
    .then((result) => {
      result.json().then((resp) => {
        setBooks(resp);
      }).catch(function (error) {
        console.log("error : ", error);
      })
    })
  }, [])

  const FilterBooks = (event) => {
    const results = books.filter(book => {
      return book.nom.startsWith(event.target.value);
    })
    const [filterBooks, setFilterBooks] = useState([])
    (results);
  }

  return (
    <>
      <input placeholder="rechercher" onChange={FilterBooks}/>
      <ListLivres books={books}/>
    </>
  );
}

export default App;
