import './ListLivres.css';

function ListLivres(props) {

  const afficheDescription = (id) => {
    document.getElementById(id).style.display = 'block';
  }

  return (
    <>
      {
        props.books.map((element) => (
          <div className='Livre'>
            <h1>{element.nom}</h1>
            <p>nb tomes</p>
            <button onClick={() => afficheDescription(element.id)}>description &#11015;</button>
            <p className='description' key={element.id}>{element.resumer}</p>
          </div>
        ))
      }
    </>
  );
}

export default ListLivres;
