import './css/App.css';
import ListCollection from './ListCollection/ListCollection.js';
import Menu from './Menu/Menu.js';
import React, { useEffect, useState } from 'react';

function App() {

  const myReducer = (state, action) => {
    switch(action.type)
    {
      case 'modif':
        return action.payload
      default:
        return state
    }

  }

  const [books, setBooks] = useReducer(myReducer, [])
  const [allBooks, setAllBooks] = useState([])

  useEffect(() => {
    fetch("https://localhost:7152/api/Livres/AllLivres")
      .then((result) => {
        result.json().then((resp) => {
          setAllBooks(resp);
          setBooks({type: "modif",payload: resp});
        }).catch(function (error) {
          console.log("error : ", error);
        })
      })
  }, [])

  const FilterBooks = (event) => {
    if (event.target.value.length != 0) {
      if (event.target.value.length == 1) {
        event.target.value = event.target.value.charAt(0).toUpperCase();
      }
      else {
        if (event.target.value.charAt(event.target.value.length - 2) == " ") {
          event.target.value = event.target.value.substr(0, event.target.value.length - 1) + event.target.value.charAt(event.target.value.length - 1).toUpperCase();
        }
      }
    }

    if (event.target.value == "") {
      setBooks(allBooks);
    }
    else {
      const results = allBooks.filter(book => {
        return book.nom.startsWith(event.target.value);
      })
      setBooks(results);
    }
  }

  return (
    <>
      <Menu />
      <div className='laList'>
        <div className='Filter'>
          <input placeholder="Rechercher dans sa collection" onChange={FilterBooks} />
          <div className='filtre'>
            <div className='actif'>Nom</div>
            <div>Edition</div>
          </div>
        </div>
        <ListCollection books={books} />
      </div>

    </>
  );
}

export default App;
