import './ListLivres.css';
import Livre from './../Livre/Livre.js';
import { useState } from 'react';

function ListLivres(props) {

  const [activeIndex, setActif] = useState(null);
  const [allBooks, setAllBooks] = useState(props.books);
  console.log(props.books);
  console.log(allBooks)


  const FilterBooks = (event) => {
    const val = event.target.value.charAt(0).toUpperCase()
    if(val == "")
    {
      props.setBooks(allBooks);
    }
    else
    {
      const results = allBooks.filter(book => {
        console.log(book.nom.startsWith(val))
        return book.nom.startsWith(val);
      })
      props.setBooks(results);
    }
  }


  const afficheDescription = (index) => {
    if (index == activeIndex) {
      setActif(null)
    }
    else {
      setActif(index)
    }
  }

  return (
    <>
      <input placeholder="rechercher" onChange={FilterBooks}/>
      {
        props.books.map((element, index) => (
          <Livre livre={element} index={index} isActive={activeIndex === index} func={afficheDescription} />
        ))
      }
    </>
  );
}

export default ListLivres;
