import './ListLivres.css';

function ListLivres(props) {

  const afficheDescription = (id) => {
    if(document.getElementById(id).classList.contains('actif'))
    {
      document.getElementById(id).style.display = "none";
    }
    else
    {
      const act = document.getElementsByTagName('p').classList.contains('actif');
      act.classList.remove('actif');
      document.getElementById(id).classList.add('actif');
    }
    
  }

  return (
    <>
      {
        props.books.map((element) => (
          <div className='Livre'>
            <h1>{element.nom}</h1>
            <p>nb tomes</p>
            <button onClick={() => afficheDescription(element.nom)}>description &#11015;</button>
            <p className='description' id={element.nom}>{element.resumer}</p>
          </div>
        ))
      }
    </>
  );
}

export default ListLivres;
