import './css/App.css';
import ListLivres from './ListLivres/ListLivres.js';
import React, {useEffect, useState} from 'react';

function App() {

  const [books, setBooks] = useState([])

  useEffect(() => {
    fetch("https://localhost:7152/api/Livres/AllLivres").then((result) => {
      result.json().then((resp) => {
        setBooks(resp);
      }).catch(function (error) {
        console.log("error : ", error);
      })
    })
  })

  return (
    <ListLivres books={books}/>
  );
}

export default App;
