import React, { createContext, useReducer, useState } from "react";

export const myAppContext = createContext({
  tabcollection: {
    collection: [],
    allBooks: []
  },
  setCollection: (action) => { }

})


export const MyAppContextProvider = ({ children }) => {

  const myReducerCollection = (state, action) => {
    switch (action.type) {
      case 'all':
        console.log(action.payload)
        return { collection: action.payload, allBooks: action.payload }

      case 'filtrerName':
        const resultsName = tabcollection.allBooks.filter(collect => {
          const collecte = collect.nom.toLowerCase();
          return collecte.startsWith(action.payload.toLowerCase());
        })

        return {collection: resultsName, allBooks: tabcollection.allBooks}

      case 'filtrerEdition':
        const resultsEdition = tabcollection.allBooks.filter(collect => {
          const collecte = collect.edition.toLowerCase();
          return collecte.startsWith(action.payload.toLowerCase());
        })
        return {collection: resultsEdition, allBooks: tabcollection.allBooks}

      default:
        return state
    }

  }

  const [tabcollection, setCollection] = useReducer(myReducerCollection, []);


  return (
    <myAppContext.Provider value={{
      tabcollection: {
        collection: [],
        allBooks: []
      },
      setCollection: setCollection
    }}>
      {children}
    </myAppContext.Provider>
  )
}
