import React, { createContext, useReducer, useState } from "react";

export const myAppContext = createContext({
    collection: [],
    setCollection: (action) => {},
    allBooks: [],
    setAllBooks: (action) => {}
})


export const MyAppContextProvider = ({children}) => {

  const [allBooks, setAllBooks] = useState([])

  const myReducerCollection = (state, action) => {
    switch(action.type)
    {
      case 'all':
        return action.payload

      case 'filtrerName':
        const resultsName = allBooks.filter(collect => {
          const collecte = collect.nom.toLowerCase();
          return collecte.startsWith(action.payload.toLowerCase());
        })
        return resultsName

      case 'filtrerEdition':
        const resultsEdition = allBooks.filter(collect => {
          const collecte = collect.edition.toLowerCase();
          return collecte.startsWith(action.payload.toLowerCase());
        })
        return resultsEdition

      default:
        return state
    }
    
  }

  const [collection, setCollection] = useReducer(myReducerCollection, []);


    return (
        <myAppContext.Provider value={{
            collection: collection,
            setCollection: setCollection,
            allBooks: allBooks,
            setAllBooks: setAllBooks
        }}>
            {children}
        </myAppContext.Provider>
    )
}
