import './css/App.css';
import ListCollection from './ListCollection/ListCollection.js';
import Menu from './Menu/Menu.js';
import React, { useEffect, useState, useReducer } from 'react';

function App() {

  const myReducerBooks = (state, action) => {
    switch(action.type)
    {
      case 'all':
        return action.payload
      case 'filtrer':
        const results = allBooks.filter(book => {
          const bk = book.nom.toLowerCase();
          return bk.startsWith(action.payload.toLowerCase());
        })
        return results
      default:
        return state
    }
  }

  const [books, setBooks] = useReducer(myReducerBooks, [])
  const [allBooks, setAllBooks] = useReducer(myReducerAllBooks, [])


  useEffect(() => {
    fetch("https://localhost:7152/api/Livres/AllLivres")
      .then((result) => {
        result.json().then((resp) => {
          setAllBooks(resp);
          setBooks({type: "all",payload: resp});
        }).catch(function (error) {
          console.log("error : ", error);
        })
      })
  }, [])

  const FilterBooks = (event) => {

    if (event.target.value == "") {
      setBooks({type: "all",payload: allBooks});
    }
    else {
      setBooks({type: "filtrer",payload: event.target.value});
    }
  }

  return (
    <>
      <Menu />
      <div className='laList'>
        <div className='Filter'>
          <input placeholder="Rechercher dans sa collection" onChange={FilterBooks} />
          <div className='filtre'>
            <div className='actif'>Nom</div>
            <div>Edition</div>
          </div>
        </div>
        <ListCollection books={books} />
      </div>

    </>
  );
}

export default App;
