import './Livre.css';

function Livre(props) {

    const afficheDescription = () => {
        props.func(props.index);
    }

    return (
        <div className='Livre'>
            <h1>{props.livre.nom}</h1>
            <p>nb tomes</p>
            <button onClick={afficheDescription}>Détails ...</button>
            <div className={props.isActive ? 'active' : 'description'}>
                <p>Edition : {props.livre.edition}</p>
                <p><i>Résumer :</i> {props.livre.resumer}</p>
            </div>
            
        </div>
    );
}

export default Livre;
